/* 
 * CSS EDITS FOR PLUGINS AND LIBRARIES
 * Styles for third-party libraries and plugins, such as dc.js and DataTables.
 */
.dc-chart g.row text {
  fill: #000;
  font-family: "Quicksand", sans-serif;
  font-weight: 600;
  font-size: 13px;
}
.dc-chart .selected path,
.dc-chart .selected circle {
